import { Component, OnInit } from '@angular/core';
import { FirebaseService } from '../../firebase.service';
import { Observable, of } from 'rxjs';
import { map, catchError } from 'rxjs/operators';

interface Reserva {
  id: string;
  nome: string;
  email: string;
  numeroPessoas: number;
  data: string;
  horario: string;
  tipoMesa: string;
}

@Component({
  selector: 'app-admin',
  templateUrl: './admin.page.html',
  styleUrls: ['./admin.page.scss'],
})
export class AdminPage implements OnInit {

  diasDisponiveis: string[] = [];
  horarios = ['18:30', '19:00', '19:30', '20:00', '20:30', '21:00', '21:30'];
  reservasAgrupadas: { [key: string]: { [key: string]: Reserva[] } } = {};
  diaSelecionado: string | null = null;
  horarioSelecionado: string | null = null;

  constructor(private firebaseService: FirebaseService) {}

  ngOnInit() {
    this.firebaseService.getReservas().pipe(
      map(actions => actions.map(a => {
        const data = a.payload.doc.data() as Reserva;
        const id = a.payload.doc.id;
        return { ...data, id }; 
      })),
      catchError(error => {
        console.error('Erro ao carregar reservas: ', error);
        return of([] as Reserva[]); 
      })
    ).subscribe(reservas => {
      this.reservasAgrupadas = {};
      this.diasDisponiveis = Array.from(new Set(reservas.map(reserva => reserva.data)));

      this.diasDisponiveis.forEach(dia => {
        this.reservasAgrupadas[dia] = this.horarios.reduce((acc: { [key: string]: Reserva[] }, horario: string) => {
          acc[horario] = reservas.filter(reserva => reserva.data === dia && reserva.horario === horario);
          return acc;
        }, {});
      });
    });
  }

  selecionarDia(dia: string) {
    this.diaSelecionado = dia;
    this.horarioSelecionado = null;
  }

  toggleReservas(horario: string) {
    if (this.horarioSelecionado === horario) {
      this.horarioSelecionado = null;
    } else {
      this.horarioSelecionado = horario;
    }
  }

  isReservasVisiveis(horario: string): boolean {
    return this.horarioSelecionado === horario;
  }
}
<ion-header>
  <ion-toolbar color="red">
    <ion-title style="text-align: center;">
      <a href="/" style="text-decoration: none; color: inherit;">
        Restaurante
      </a>
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content fullscreen class="ion-padding">
  <div *ngIf="!diaSelecionado" class="dias-container">
    <ion-list>
      <ion-item *ngFor="let dia of diasDisponiveis" (click)="selecionarDia(dia)" class="dia-item">
        <ion-button fill="solid" expand="block" color="danger" class="dia-button">
          {{ dia | date: 'dd/MM' }}
        </ion-button>
      </ion-item>
    </ion-list>
  </div>
  
  <div *ngIf="diaSelecionado">
    <ion-button (click)="diaSelecionado = null" color="danger" class="back-button">Voltar</ion-button>
    <div class="horarios-container">
      <ion-button 
        *ngFor="let horario of horarios" 
        (click)="toggleReservas(horario)" 
        color="danger"
        class="horario-button">
        {{ horario }}
      </ion-button>
    </div>
    <ion-list lines="full">
      <div *ngFor="let horario of horarios">
        <div *ngIf="isReservasVisiveis(horario)">
          <ion-item *ngFor="let reserva of reservasAgrupadas[diaSelecionado][horario]">
            <ion-label>
              <h2>{{ reserva.nome }}</h2>
              <p>Email: {{ reserva.email }}</p>
              <p>Número de Pessoas: {{ reserva.numeroPessoas }}</p>
              <p>Data: {{ reserva.data | date: 'dd/MM/yyyy' }}</p>
              <p>Horário: {{ reserva.horario }}</p>
              <p>Tipo de Mesa: {{ reserva.tipoMesa }}</p>
            </ion-label>
          </ion-item>
        </div>
      </div>
    </ion-list>
  </div>
</ion-content>
