import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';


@Component({
  selector: 'app-reservas',
  templateUrl: './reservas.page.html',
  styleUrls: ['./reservas.page.scss'],
})
export class ReservasPage {

  currentDate: string;
  constructor(private navCtrl: NavController) {
    this.currentDate = new Date().toDateString();
  }


  irParaLogin() {
    this.navCtrl.navigateForward('/login');
  }

  submitReserva() {
    // Lógica para enviar a reserva

  }




}

<ion-header>
  <ion-toolbar color="primary"> 
    <ion-title style="text-align: center;">
      <a href="/" style="text-decoration: none; color: white;">Nome do Restaurante</a>
    </ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="irParaLogin()">
        <ion-icon slot="icon-only" name="log-in"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content fullscreen class="ion-padding"> 
  <form (submit)="submitReserva()" class="reservation-form"> 
    <ion-list lines="full">
      </ion-list>
    <ion-item>
      <ion-label>Data</ion-label>
      <ion-datetime presentation="date" display-format="DD/MM/YYYY" picker-format="DD MMM YYYY" 
                    min="{{ currentDate }}" required></ion-datetime>
    </ion-item>
    <ion-item>
          <ion-label position="floating">Horário Preferencial</ion-label>
      </ion-item>
    <ion-radio-group> 
          <ion-item *ngFor="let time of ['18:45', '19:00', '20:00', '21:00', '21:50']">
            <ion-label>{{ time }}</ion-label>
            <ion-radio slot="end" value="{{ time }}"></ion-radio>
          </ion-item>
        </ion-radio-group>
    <ion-button type="submit" expand="block" class="submit-button" color="danger">
      Reservar
    </ion-button>
  </form>
</ion-content>
