import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';
import { FirebaseService } from '../services/firebase.service';

@Component({
  selector: 'app-reservas',
  templateUrl: './reservas.page.html',
  styleUrls: ['./reservas.page.scss'],
})
export class ReservasPage {

  currentDate: string;

  reserva: any = {
    nome: '',
    email: '',
    numeroPessoas: null,
    data: '',
    horario: '',
    tipoMesa: ''
  };

  constructor(private navCtrl: NavController, private firebaseService: FirebaseService) {
    const today = new Date();
    this.currentDate = today.toISOString().split('T')[0];
  }

  submitReserva() {
    this.firebaseService.addReserva(this.reserva)
      .then(() => {
        console.log('Reserva adicionada com sucesso');
        // Resetar o formulário ou mostrar uma mensagem de sucesso
      })
      
  }

}
