import { Component } from '@angular/core';
import { NavController, ToastController } from '@ionic/angular';
import { FirebaseService } from '../../firebase.service'; // Substitua pelo caminho correto do seu serviço Firebase
import { Observable } from 'rxjs';

@Component({
  selector: 'app-reservas',
  templateUrl: './reservas.page.html',
  styleUrls: ['./reservas.page.scss'],
})
export class ReservasPage {

  currentDate: string;
  reserva = {
    nome: '',
    email: '',
    numeroPessoas: '',
    tipoMesa: '',
    data: '',
    horario: ''
  };

  limitesMesas$: Observable<any[]>;

  constructor(
    private navCtrl: NavController,
    private firebaseService: FirebaseService,
    private toastController: ToastController
  ) {
    this.currentDate = new Date().toISOString(); // Inicializa com a data atual
    this.limitesMesas$ = this.firebaseService.getLimiteMesas(); // Obtém os limites de mesas do Firebase
  }

  irParaLogin() {
    this.navCtrl.navigateForward('/login');
  }

  async submitReserva() {
    try {
      const disponivel = await this.firebaseService.verificarDisponibilidade(this.reserva.tipoMesa).toPromise();
      if (!disponivel) {
        this.mostrarToast('Não há mesas disponíveis para este tipo. Por favor, escolha outro tipo.');
        return;
      }

      // Adicionar reserva ao Firebase
      await this.firebaseService.addReserva({
        nome: this.reserva.nome,
        email: this.reserva.email,
        numeroPessoas: this.reserva.numeroPessoas,
        tipoMesa: this.reserva.tipoMesa,
        data: this.reserva.data,
        horario: this.reserva.horario
      });

      // Mostrar mensagem de sucesso
      this.mostrarToast('Reserva realizada com sucesso!');
      
      // Redirecionar para outra página ou fazer alguma ação após a reserva ser realizada
      // this.navCtrl.navigateForward('/outra-pagina');

    } catch (error) {
      console.error('Erro ao adicionar reserva:', error);
      this.mostrarToast('Erro ao realizar a reserva. Por favor, tente novamente mais tarde.');
    }
  }

  async mostrarToast(mensagem: string) {
    const toast = await this.toastController.create({
      message: mensagem,
      duration: 3000, // 3 segundos
      position: 'bottom'
    });
    toast.present();
  }

}
